    // Function to save the module settings/configuration in setting table
    public function editSetting($code, $data, $store_id = 0) {
        //Delete Settings
        $this->deleteSetting($code, $store_id);
        foreach ($data as $key => $value) {
            if (substr($key, 0, strlen($code)) == $code) {
                if (!is_array($value)) {
                    $this->db->query("INSERT INTO " . DB_PREFIX . "setting SET store_id = '" . (int) $store_id . "', `code` = '" . $this->db->escape($code) . "', `key` = '" . $this->db->escape($key) . "', `value` = '" . $this->db->escape($value) . "'");
                } else {
                    if (VERSION < 2.2) {
                        $this->db->query("INSERT INTO " . DB_PREFIX . "setting SET store_id = '" . (int) $store_id . "', `code` = '" . $this->db->escape($code) . "', `key` = '" . $this->db->escape($key) . "', `value` = '" . $this->db->escape(serialize($value)) . "', serialized = '1'");
                    } else {
                        $this->db->query("INSERT INTO " . DB_PREFIX . "setting SET store_id = '" . (int) $store_id . "', `code` = '" . $this->db->escape($code) . "', `key` = '" . $this->db->escape($key) . "', `value` = '" . $this->db->escape(json_encode($value)) . "', serialized = '1'");
                    }
                }
            }
        }
        return true;
    }

    // Function to get the module configuration details from setting table 
    public function getSetting($code, $store_id = 0) {
        $setting_data = array();
        $query = $this->db->query("SELECT * FROM " . DB_PREFIX . "setting WHERE store_id = '" . (int) $store_id . "' AND `code` = '" . $this->db->escape($code) . "'");

        //Iteration of the data to get the proper formatted results
        foreach ($query->rows as $result) {
            if (!$result['serialized']) {
                $setting_data[$result['key']] = $result['value'];
            } else {
                if (VERSION < 2.2) {
                    $setting_data[$result['key']] = unserialize($result['value']);
                } else {
                    $setting_data[$result['key']] = json_decode($result['value'], true);
                }
            }
        }
        return $setting_data;
    }

    // Function to delete the module configuration/setting from setting table */
    public function deleteSetting($code, $store_id = 0) {
        $this->db->query("DELETE FROM " . DB_PREFIX . "setting WHERE store_id = '" . (int) $store_id . "' AND `code` = '" . $this->db->escape($code) . "'");
    }

    public function checkInstalled($code) {
        $query = $this->db->query("SELECT * FROM " . DB_PREFIX . "extension WHERE `code` = '" . $this->db->escape($code) . "' ORDER BY code");
        return $query->num_rows;
    }

